<?phpabstract class Rss_FeedData{  public $title;  public $description;  public $atomLink;  public function __construct($title, $description, $atomLink)  {    $this->title = $title;    $this->description = $description;    $this->atomLink = $atomLink;  }  abstract public function getItems();  abstract public function getItemTitle($r);  abstract public function getItemDescription($r);  abstract public function getItemLink($r);  abstract public function getItemDate($r);  abstract public function getItemId($r);  abstract public function getItemEnclosure($r);}?>